import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Mic, MicOff, Volume2, MessageCircle } from 'lucide-react';
import { useVoiceChat } from '../hooks/useVoiceChat';
import { useVoiceStore } from '../stores/voiceStore';
import { format } from 'date-fns';

const VoiceInterface: React.FC = () => {
  const { toggleRecording, isRecording, isProcessing, connectionStatus, isConnected } = useVoiceChat();
  const { messages, clearMessages } = useVoiceStore();

  const getConnectionStatusColor = () => {
    switch (connectionStatus) {
      case 'connected': return 'text-green-500';
      case 'connecting': return 'text-yellow-500';
      case 'error': return 'text-red-500';
      default: return 'text-gray-500';
    }
  };

  const getConnectionStatusText = () => {
    switch (connectionStatus) {
      case 'connected': return 'Connected';
      case 'connecting': return 'Connecting...';
      case 'error': return 'Connection Error';
      default: return 'Disconnected';
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gradient-to-br from-blue-50 via-white to-cyan-50">
      {/* Header */}
      <motion.div
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        className="bg-white/80 backdrop-blur-md border-b border-blue-100 p-4 shadow-sm"
      >
        <div className="max-w-4xl mx-auto flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="bg-gradient-to-r from-blue-500 to-cyan-500 p-2 rounded-xl">
              <MessageCircle className="w-6 h-6 text-white" />
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900">DocTalk AI</h1>
              <p className="text-sm text-gray-600">Your Voice Assistant for Medical Appointments</p>
            </div>
          </div>
          
          <div className="flex items-center space-x-2">
            <div className={`w-2 h-2 rounded-full ${connectionStatus === 'connected' ? 'bg-green-400' : 'bg-gray-400'} animate-pulse`} />
            <span className={`text-sm font-medium ${getConnectionStatusColor()}`}>
              {getConnectionStatusText()}
            </span>
          </div>
        </div>
      </motion.div>

      {/* Chat Area */}
      <div className="flex-1 overflow-hidden">
        <div className="max-w-4xl mx-auto h-full flex flex-col p-4">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto space-y-4 mb-4">
            <AnimatePresence>
              {messages.length === 0 ? (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="text-center py-12"
                >
                  <div className="bg-gradient-to-r from-blue-500 to-cyan-500 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                    <Mic className="w-8 h-8 text-white" />
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">Welcome to DocTalk AI</h3>
                  <p className="text-gray-600 max-w-md mx-auto">
                    Press the microphone button and start speaking to book, reschedule, or cancel medical appointments.
                  </p>
                  <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4 max-w-2xl mx-auto text-sm">
                    <div className="bg-white rounded-lg p-4 shadow-sm border border-blue-100">
                      <h4 className="font-medium text-blue-900 mb-1">Book Appointment</h4>
                      <p className="text-gray-600">"I want to see Dr. Smith tomorrow at 10 AM"</p>
                    </div>
                    <div className="bg-white rounded-lg p-4 shadow-sm border border-green-100">
                      <h4 className="font-medium text-green-900 mb-1">Check Availability</h4>
                      <p className="text-gray-600">"What times are available with Dr. Johnson?"</p>
                    </div>
                    <div className="bg-white rounded-lg p-4 shadow-sm border border-purple-100">
                      <h4 className="font-medium text-purple-900 mb-1">Cancel Appointment</h4>
                      <p className="text-gray-600">"I need to cancel my appointment"</p>
                    </div>
                  </div>
                </motion.div>
              ) : (
                messages.map((message) => (
                  <motion.div
                    key={message.id}
                    initial={{ opacity: 0, y: 20, scale: 0.95 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, scale: 0.95 }}
                    className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div
                      className={`max-w-xs lg:max-w-md px-4 py-3 rounded-2xl shadow-sm message-bubble ${
                        message.type === 'user'
                          ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white'
                          : 'bg-white border border-gray-200 text-gray-900'
                      }`}
                    >
                      <p className="text-sm leading-relaxed">{message.content}</p>
                      <div className={`flex items-center justify-between mt-2 ${
                        message.type === 'user' ? 'text-blue-100' : 'text-gray-500'
                      }`}>
                        <span className="text-xs">
                          {format(new Date(message.timestamp), 'HH:mm')}
                        </span>
                        {message.isAudio && (
                          <Volume2 className="w-3 h-3 ml-2" />
                        )}
                      </div>
                    </div>
                  </motion.div>
                ))
              )}
            </AnimatePresence>

            {/* Processing Indicator */}
            <AnimatePresence>
              {isProcessing && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  className="flex justify-start"
                >
                  <div className="bg-white border border-gray-200 rounded-2xl px-4 py-3 shadow-sm">
                    <div className="flex items-center space-x-2">
                      <div className="typing-indicator">
                        <span className="w-1 h-1 bg-gray-400 rounded-full inline-block">•</span>
                        <span className="w-1 h-1 bg-gray-400 rounded-full inline-block">•</span>
                        <span className="w-1 h-1 bg-gray-400 rounded-full inline-block">•</span>
                      </div>
                      <span className="text-xs text-gray-500">DocTalk AI is thinking...</span>
                    </div>
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </div>

          {/* Voice Controls */}
          <motion.div
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            className="flex items-center justify-center space-x-4"
          >
            {/* Clear Chat */}
            {messages.length > 0 && (
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={clearMessages}
                className="btn-secondary"
              >
                Clear Chat
              </motion.button>
            )}

            {/* Main Microphone Button */}
            <motion.button
              whileHover={{ scale: isRecording ? 1 : 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={toggleRecording}
              disabled={!isConnected}
              className={`relative w-20 h-20 rounded-full shadow-xl transition-all duration-300 ${
                isRecording
                  ? 'bg-red-500 hover:bg-red-600 recording-pulse'
                  : 'bg-gradient-to-r from-blue-500 to-blue-600 hover:shadow-glow'
              } ${!isConnected ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              <AnimatePresence mode="wait">
                {isRecording ? (
                  <motion.div
                    key="recording"
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                    exit={{ scale: 0 }}
                    className="flex items-center justify-center w-full h-full"
                  >
                    <MicOff className="w-8 h-8 text-white" />
                  </motion.div>
                ) : (
                  <motion.div
                    key="idle"
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                    exit={{ scale: 0 }}
                    className="flex items-center justify-center w-full h-full"
                  >
                    <Mic className="w-8 h-8 text-white" />
                  </motion.div>
                )}
              </AnimatePresence>

              {/* Voice Waves Animation */}
              {isRecording && (
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="flex space-x-1">
                    {[...Array(5)].map((_, i) => (
                      <div
                        key={i}
                        className="voice-wave bg-white/30 w-1 rounded-full"
                      />
                    ))}
                  </div>
                </div>
              )}
            </motion.button>

            {/* Status Text */}
            <div className="text-center">
              <p className="text-sm font-medium text-gray-900">
                {isRecording ? 'Listening...' : isProcessing ? 'Processing...' : 'Tap to speak'}
              </p>
              <p className="text-xs text-gray-500 mt-1">
                {isRecording ? 'Release to send' : 'Hold and speak naturally'}
              </p>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default VoiceInterface;
