import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { 
  Users, 
  UserCheck, 
  Calendar, 
  Activity,
  TrendingUp,
  BarChart3,
  PieChart,
  Search,
  Plus,
  Database,
  RefreshCw,
  XCircle,
  AlertCircle,
  Wifi,
  WifiOff
} from 'lucide-react';
import { api, testConnection } from '../config/api';

interface Statistics {
  patients: {
    total_patients: number;
    new_this_month: number;
    by_gender: Record<string, number>;
    by_age_group: Record<string, number>;
  };
  doctors: {
    total_doctors: number;
    available_doctors: number;
    by_specialty: Record<string, number>;
    by_department: Record<string, number>;
    by_experience: Record<string, number>;
  };
  appointments: {
    today: number;
    this_week: number;
    this_month: number;
    by_status: Record<string, number>;
    by_doctor: Record<string, number>;
  };
}

interface DashboardProps {
  onNavigate: (section: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ onNavigate }) => {
  const [statistics, setStatistics] = useState<Statistics | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isCreatingSample, setIsCreatingSample] = useState(false);
  const [isClearingDb, setIsClearingDb] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState<'connected' | 'disconnected' | 'checking'>('checking');

  const checkConnection = useCallback(async () => {
    if (connectionStatus === 'checking') return; // Prevent multiple simultaneous checks
    
    setConnectionStatus('checking');
    try {
      const isConnected = await testConnection();
      setConnectionStatus(isConnected ? 'connected' : 'disconnected');
      return isConnected;
    } catch (error) {
      setConnectionStatus('disconnected');
      return false;
    }
  }, [connectionStatus]);

  const fetchStatistics = useCallback(async () => {
    if (loading) return; // Prevent multiple simultaneous fetches
    
    try {
      setLoading(true);
      setError(null);
      
      // Fetch all statistics in parallel
      const [patientsData, doctorsData, appointmentsData] = await Promise.all([
        api.getPatientStats(),
        api.getDoctorStats(),
        api.getAppointmentStats()
      ]);

      setStatistics({
        patients: patientsData,
        doctors: doctorsData,
        appointments: appointmentsData
      });
      
      setConnectionStatus('connected');
    } catch (err) {
      console.error('Error fetching statistics:', err);
      const errorMessage = err instanceof Error ? err.message : 'Unknown error occurred';
      setError(`Failed to load dashboard: ${errorMessage}`);
      setConnectionStatus('disconnected');
    } finally {
      setLoading(false);
    }
  }, [loading]);

  const createSampleData = async () => {
    if (isCreatingSample) return; // Prevent double-clicking
    
    try {
      setIsCreatingSample(true);
      await api.createSampleData();
      
      alert('Sample data created successfully!');
      
      // Refresh statistics after a short delay
      setTimeout(() => {
        fetchStatistics();
      }, 1000);
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      alert(`Error creating sample data: ${errorMessage}`);
    } finally {
      setIsCreatingSample(false);
    }
  };

  const clearDatabase = async () => {
    if (isClearingDb) return; // Prevent double-clicking
    
    if (!window.confirm('Are you sure you want to clear ALL database data? This cannot be undone!')) {
      return;
    }
    
    try {
      setIsClearingDb(true);
      await api.clearDatabase();
      
      alert('Database cleared successfully!');
      
      // Reset statistics and refresh
      setStatistics(null);
      setTimeout(() => {
        fetchStatistics();
      }, 1000);
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      alert(`Error clearing database: ${errorMessage}`);
    } finally {
      setIsClearingDb(false);
    }
  };

  // Initial load - only run once
  useEffect(() => {
    let mounted = true;
    
    const initialLoad = async () => {
      if (!mounted) return;
      
      try {
        const isConnected = await checkConnection();
        if (!mounted) return;
        
        if (isConnected) {
          await fetchStatistics();
        }
      } catch (error) {
        if (mounted) {
          setError('Failed to connect to backend server');
          setLoading(false);
        }
      }
    };
    
    initialLoad();
    
    return () => {
      mounted = false;
    };
  }, []); // Empty dependency array - only run once

  // Periodic connection check - separate from data fetching
  useEffect(() => {
    const connectionCheckInterval = setInterval(() => {
      if (connectionStatus !== 'checking') {
        checkConnection();
      }
    }, 60000); // Check every 60 seconds (not 30)
    
    return () => clearInterval(connectionCheckInterval);
  }, [checkConnection, connectionStatus]);

  // Connection Status Component
  const ConnectionStatus = () => (
    <div className="flex items-center space-x-2">
      {connectionStatus === 'connected' && (
        <>
          <Wifi className="w-4 h-4 text-green-500" />
          <span className="text-sm text-green-600 font-medium">Connected</span>
        </>
      )}
      {connectionStatus === 'disconnected' && (
        <>
          <WifiOff className="w-4 h-4 text-red-500" />
          <span className="text-sm text-red-600 font-medium">Disconnected</span>
        </>
      )}
      {connectionStatus === 'checking' && (
        <>
          <RefreshCw className="w-4 h-4 text-yellow-500 animate-spin" />
          <span className="text-sm text-yellow-600 font-medium">Checking...</span>
        </>
      )}
    </div>
  );

  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center h-64 space-y-4">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        <div className="text-center">
          <p className="text-gray-700 font-medium">Loading Dashboard...</p>
          <p className="text-sm text-gray-500">Connecting to backend server</p>
        </div>
        <ConnectionStatus />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-2xl mx-auto">
          <XCircle className="w-12 h-12 text-red-500 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-red-800 mb-2">Connection Error</h3>
          <p className="text-red-600 mb-4 text-sm">{error}</p>
          
          <div className="mb-4">
            <ConnectionStatus />
          </div>
          
          <div className="space-y-3">
            <div className="flex flex-col sm:flex-row gap-2 justify-center">
              <button 
                onClick={() => window.location.reload()}
                className="btn-primary flex items-center justify-center"
              >
                <RefreshCw className="w-4 h-4 mr-2" />
                Reload Page
              </button>
              
              <button 
                onClick={checkConnection}
                className="btn-secondary flex items-center justify-center"
                disabled={connectionStatus === 'checking'}
              >
                <Wifi className="w-4 h-4 mr-2" />
                Test Connection
              </button>
            </div>
            
            <div className="text-xs text-gray-500 space-y-1">
              <p><strong>Troubleshooting:</strong></p>
              <p>1. Make sure your backend server is running: <code>uvicorn main:app --reload</code></p>
              <p>2. Check if the server is accessible at: <code>http://localhost:8000/health</code></p>
              <p>3. Verify there are no firewall or antivirus blocks</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!statistics || (
    statistics.patients.total_patients === 0 && 
    statistics.doctors.total_doctors === 0 && 
    statistics.appointments.today === 0
  )) {
    return (
      <div className="text-center py-12">
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 max-w-md mx-auto">
          <AlertCircle className="w-12 h-12 text-yellow-500 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-yellow-800 mb-2">No Data Available</h3>
          <p className="text-yellow-600 mb-4 text-sm">The database appears to be empty.</p>
          <button 
            onClick={createSampleData} 
            className="btn-primary"
            disabled={isCreatingSample}
          >
            {isCreatingSample ? (
              <>
                <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                Creating...
              </>
            ) : (
              <>
                <Database className="w-4 h-4 mr-2" />
                Create Sample Data
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  const quickActions = [
    { icon: Plus, label: 'New Patient', action: () => onNavigate('patients'), color: 'bg-blue-500' },
    { icon: Plus, label: 'New Doctor', action: () => onNavigate('doctors'), color: 'bg-green-500' },
    { icon: Calendar, label: 'Schedule Appointment', action: () => onNavigate('appointments'), color: 'bg-purple-500' },
    { icon: Search, label: 'Voice Chat', action: () => onNavigate('voice'), color: 'bg-orange-500' }
  ];

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">DocTalk AI Dashboard</h1>
          <div className="flex items-center space-x-4 mt-2">
            <p className="text-gray-600">Comprehensive medical practice management</p>
            <ConnectionStatus />
          </div>
        </div>
        
        <div className="flex flex-wrap gap-3">
          <button
            onClick={() => window.location.reload()}
            disabled={loading}
            className="flex items-center gap-2 px-3 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors disabled:opacity-50"
            title="Refresh page"
          >
            <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
            <span className="hidden sm:block">Refresh</span>
          </button>
          
          <button
            onClick={createSampleData}
            disabled={isCreatingSample || connectionStatus === 'disconnected'}
            className="flex items-center gap-2 px-3 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50"
            title="Create sample patients, doctors, and appointments"
          >
            <Database className={`h-4 w-4 ${isCreatingSample ? 'animate-spin' : ''}`} />
            <span className="hidden sm:block">{isCreatingSample ? 'Creating...' : 'Sample Data'}</span>
          </button>
          
          <button
            onClick={clearDatabase}
            disabled={isClearingDb || connectionStatus === 'disconnected'}
            className="flex items-center gap-2 px-3 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors disabled:opacity-50"
            title="Clear all data from database"
          >
            <XCircle className={`h-4 w-4 ${isClearingDb ? 'animate-spin' : ''}`} />
            <span className="hidden sm:block">{isClearingDb ? 'Clearing...' : 'Clear DB'}</span>
          </button>
        </div>
      </div>

      {/* Key Metrics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="card bg-gradient-to-r from-blue-500 to-blue-600 text-white cursor-pointer hover:shadow-lg transition-shadow"
          onClick={() => onNavigate('patients')}
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-100">Total Patients</p>
              <h3 className="text-3xl font-bold">{statistics.patients.total_patients}</h3>
              <p className="text-blue-200 text-sm">+{statistics.patients.new_this_month} this month</p>
            </div>
            <Users className="w-12 h-12 text-blue-200" />
          </div>
        </motion.div>

        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="card bg-gradient-to-r from-green-500 to-green-600 text-white cursor-pointer hover:shadow-lg transition-shadow"
          onClick={() => onNavigate('doctors')}
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-green-100">Available Doctors</p>
              <h3 className="text-3xl font-bold">{statistics.doctors.available_doctors}</h3>
              <p className="text-green-200 text-sm">of {statistics.doctors.total_doctors} total</p>
            </div>
            <UserCheck className="w-12 h-12 text-green-200" />
          </div>
        </motion.div>

        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="card bg-gradient-to-r from-purple-500 to-purple-600 text-white cursor-pointer hover:shadow-lg transition-shadow"
          onClick={() => onNavigate('appointments')}
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-100">Today's Appointments</p>
              <h3 className="text-3xl font-bold">{statistics.appointments.today}</h3>
              <p className="text-purple-200 text-sm">{statistics.appointments.this_week} this week</p>
            </div>
            <Calendar className="w-12 h-12 text-purple-200" />
          </div>
        </motion.div>

        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          className="card bg-gradient-to-r from-orange-500 to-orange-600 text-white cursor-pointer hover:shadow-lg transition-shadow"
          onClick={() => onNavigate('voice')}
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-orange-100">Monthly Appointments</p>
              <h3 className="text-3xl font-bold">{statistics.appointments.this_month}</h3>
              <p className="text-orange-200 text-sm">Voice booking available</p>
            </div>
            <Activity className="w-12 h-12 text-orange-200" />
          </div>
        </motion.div>
      </div>

      {/* Detailed Statistics Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
        {/* Patient Demographics */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="card"
        >
          <div className="flex items-center space-x-3 mb-4">
            <div className="bg-blue-100 p-2 rounded-lg">
              <PieChart className="w-5 h-5 text-blue-600" />
            </div>
            <h3 className="text-lg font-semibold">Patient Demographics</h3>
          </div>
          
          <div className="space-y-3">
            {Object.keys(statistics.patients.by_gender).length > 0 && (
              <div>
                <h4 className="font-medium text-gray-700 mb-2">By Gender</h4>
                {Object.entries(statistics.patients.by_gender).map(([gender, count]) => (
                  <div key={gender} className="flex justify-between items-center py-1">
                    <span className="text-gray-600 capitalize">{gender || 'Unknown'}</span>
                    <span className="font-medium">{count}</span>
                  </div>
                ))}
              </div>
            )}
            
            {Object.keys(statistics.patients.by_age_group).length > 0 && (
              <div>
                <h4 className="font-medium text-gray-700 mb-2">By Age Group</h4>
                {Object.entries(statistics.patients.by_age_group).map(([age, count]) => (
                  <div key={age} className="flex justify-between items-center py-1">
                    <span className="text-gray-600">{age}</span>
                    <span className="font-medium">{count}</span>
                  </div>
                ))}
              </div>
            )}
            
            {Object.keys(statistics.patients.by_gender).length === 0 && 
             Object.keys(statistics.patients.by_age_group).length === 0 && (
              <p className="text-gray-500 text-sm italic">No demographic data available</p>
            )}
          </div>
        </motion.div>

        {/* Doctor Specialties */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
          className="card"
        >
          <div className="flex items-center space-x-3 mb-4">
            <div className="bg-green-100 p-2 rounded-lg">
              <BarChart3 className="w-5 h-5 text-green-600" />
            </div>
            <h3 className="text-lg font-semibold">Doctor Specialties</h3>
          </div>
          
          <div className="space-y-3">
            {Object.keys(statistics.doctors.by_specialty).length > 0 ? (
              Object.entries(statistics.doctors.by_specialty).map(([specialty, count]) => (
                <div key={specialty} className="flex justify-between items-center py-2 border-b border-gray-100 last:border-b-0">
                  <span className="text-gray-700">{specialty}</span>
                  <span className="font-medium text-green-600">{count}</span>
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-sm italic">No specialty data available</p>
            )}
          </div>
        </motion.div>

        {/* Appointment Status */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6 }}
          className="card"
        >
          <div className="flex items-center space-x-3 mb-4">
            <div className="bg-purple-100 p-2 rounded-lg">
              <TrendingUp className="w-5 h-5 text-purple-600" />
            </div>
            <h3 className="text-lg font-semibold">Appointment Status</h3>
          </div>
          
          <div className="space-y-3">
            {Object.keys(statistics.appointments.by_status).length > 0 ? (
              Object.entries(statistics.appointments.by_status).map(([status, count]) => {
                const getStatusColor = (status: string) => {
                  switch (status) {
                    case 'scheduled': return 'bg-blue-500';
                    case 'completed': return 'bg-green-500';
                    case 'cancelled': return 'bg-red-500';
                    case 'rescheduled': return 'bg-yellow-500';
                    default: return 'bg-gray-500';
                  }
                };
                
                return (
                  <div key={status} className="flex justify-between items-center py-2">
                    <div className="flex items-center space-x-2">
                      <div className={`w-3 h-3 rounded-full ${getStatusColor(status)}`}></div>
                      <span className="text-gray-700 capitalize">{status}</span>
                    </div>
                    <span className="font-medium">{count}</span>
                  </div>
                );
              })
            ) : (
              <p className="text-gray-500 text-sm italic">No appointment data available</p>
            )}
          </div>
        </motion.div>
      </div>

      {/* Quick Access Buttons */}
      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.7 }}
        className="card"
      >
        <h3 className="text-lg font-semibold mb-4">Quick Access</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {quickActions.map((action, index) => (
            <motion.button
              key={index}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={action.action}
              className="p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors"
            >
              <action.icon className="w-8 h-8 text-gray-400 mx-auto mb-2" />
              <p className="text-sm font-medium text-gray-700">{action.label}</p>
            </motion.button>
          ))}
        </div>
      </motion.div>
    </div>
  );
};

export default Dashboard;
